pub enum Shape {
    Rectangle {
        width: u32,
        height: u32,
    },
    Circle {
        radius: u32,
    },
    Line {
        start: Point,
        points: Vec<LinePoint>,
    },
}

pub enum LinePoint {
    Straight {
        point: Point,
    },
    QuadraticBezierCurve {
        point: Point,
        curve: Point,
    },
    CubicBezierCurve {
        point: Point,
        curve_a: Point,
        curve_b: Point,
    },
}
